mapper.select:
  	select 
	* 
	from t_c_health_code_apply a

		LEFT JOIN 
		t_c_commuity c
		on a.community_code=c.VILLAGE_CODE
		and a.district_code =c.COUNTY_CODE
		and a.street_code=c.STREET_CODE




			AND a.district_code = #{districtCode,jdbcType=VARCHAR}


			AND a.street_code = #{streetCode,jdbcType=VARCHAR}


			AND a.community_code = #{communityCode,jdbcType=VARCHAR}


			AND a.is_healthy = #{isHealthy,jdbcType=VARCHAR}



			AND a.housing_name like concat('%',#{housingName,jdbcType=VARCHAR},'%') 



			AND a.cert_Code = #{certCode,jdbcType=VARCHAR}


			AND a.phone = #{phone,jdbcType=VARCHAR}


			AND a.user_Name = #{userName,jdbcType=VARCHAR}


			AND a.create_time >=
			STR_TO_DATE(#{startTime,jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')


			AND a.create_time <=
			STR_TO_DATE(#{endTime,jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')


			AND c.cmmty_type=#{cmmtyType,jdbcType=VARCHAR}


		order by a.create_time
    	LIMIT
		${(pageBegin-pageBegin)+(pageBegin-1)},${pageEnd-pageBegin+1}

  select 
	count(1)
	from t_c_health_code_apply a

		LEFT JOIN 
		t_c_commuity c
		on a.community_code=c.VILLAGE_CODE
		and a.district_code =c.COUNTY_CODE
		and a.street_code=c.STREET_CODE




			AND a.district_code = #{districtCode,jdbcType=VARCHAR}


			AND a.street_code = #{streetCode,jdbcType=VARCHAR}


			AND a.community_code = #{communityCode,jdbcType=VARCHAR}


			AND a.is_healthy = #{isHealthy,jdbcType=VARCHAR}



			AND a.housing_name like concat('%',#{housingName,jdbcType=VARCHAR},'%') 



			AND a.cert_Code = #{certCode,jdbcType=VARCHAR}


			AND a.phone = #{phone,jdbcType=VARCHAR}


			AND a.user_Name = #{userName,jdbcType=VARCHAR}


			AND a.create_time >=
			STR_TO_DATE(#{startTime,jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')


			AND a.create_time <=
			STR_TO_DATE(#{endTime,jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')


			AND c.cmmty_type=#{cmmtyType,jdbcType=VARCHAR}

select cert_code, real_name user_name, community_id, is_health is_healthy, create_time, update_time, check_person from t_c_health_code_sweep_record where cert_code= #{certCode,jdbcType=VARCHAR}
AND create_time >= STR_TO_DATE(#{startTime,jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')
AND create_time <= STR_TO_DATE(#{endTime,jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')
union ALL select cert_code, real_name user_name, community_id, is_health is_healthy, create_time, update_time, check_person from t_c_health_code_sweep_rec_his where cert_code= #{certCode,jdbcType=VARCHAR}
AND create_time >= STR_TO_DATE(#{startTime,jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')
AND create_time <= STR_TO_DATE(#{endTime,jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')
ORDER BY create_time desc LIMIT ${(pageBegin-pageBegin)+(pageBegin-1)},${pageEnd-pageBegin+1}

select count(1) from (select cert_code, create_time from t_c_health_code_sweep_record where cert_code= #{certCode,jdbcType=VARCHAR}
AND create_time >= STR_TO_DATE(#{startTime,jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')
AND create_time <= STR_TO_DATE(#{endTime,jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')
union ALL select cert_code, create_time check_person from t_c_health_code_sweep_rec_his where cert_code= #{certCode,jdbcType=VARCHAR}
AND create_time >= STR_TO_DATE(#{startTime,jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')
AND create_time <= STR_TO_DATE(#{endTime,jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')
) a
